let countryoption = "<option value='' selected>Select Country</option>";
let stateoption = "<option value='' selected>Select state after country selection</option>";
countries.forEach(setcountry);
function setcountry(countryname, index) {
	if(countryname==="India"){
  countryoption += "<option value='"+countryname+"' selected>"+countryname+"</option>"; 
	}else{
		countryoption += "<option value='"+countryname+"'>"+countryname+"</option>"; 
	}
	//countryoption += "states['"+index+"']=new Array("") ; <br>"; 
}

